import React, { Component } from "react";
import API from "../utils/api";
import { Container, Row, Col } from "../components/GridLayout";
import { BookList, BookListItem } from "../components/BookList";
import { Input, SearchButton } from "../components/InputBooks";

class Search extends Component {
  // instatiate state for list of books retrieved from googlebooks api and bookSearch value
  state = {
    books: [],
    bookSearch: "",
  };

  handleInputChange = (event) => {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  };

  handleFormSubmit = (event) => {
    event.preventDefault();

    API.searchBooks(this.state.bookSearch)
      .then((res) => {
        this.setState({ books: res.data.items }, function () {
          console.log(this.state.books);
        });
      })
      .catch((err) => console.log(err));
  };
  render() {
    return (
      <div>
        <Container>
          <Row>
            <Col size="md-12">
              <form>
                <Container>
                  <Row>
                    <Col size="xs-12 sm-12">
                      <Input
                        name="bookSearch"
                        value={this.state.bookSearch}
                        onChange={this.handleInputChange}
                        placeholder="Search for a Book"
                      />
                    </Col>
                  </Row>
                  <Row>
                    <Col size="xs-12 sm-12">
                      <SearchButton
                        onClick={this.handleFormSubmit}
                        type="success"
                        className="input-lg"
                      >
                        Search
                      </SearchButton>
                    </Col>
                  </Row>
                </Container>
              </form>
            </Col>
          </Row>
          <Row>
            <Col size="xs-12">
              <BookList>
                {this.state.books.map((book) => {
                  return (
                    <BookListItem
                      key={book.id}
                      title={book.volumeInfo.title}
                      authors={book.volumeInfo.authors}
                      link={book.volumeInfo.infoLink}
                      description={book.volumeInfo.description}
                      // if no imageLinks then use placeholder image
                    />
                  );
                })}
              </BookList>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default Search;
